       <div className='player-name'>{player}</div> 
       <div className='position-and-team'>{position} - &nbsp;
       {props.team}</div>
       <a href="#">
          <span class="glyphicon glyphicon-plus" onClick={() => addPlayer(player,position,team)}></span>
        </a>      
           </div>)
}

const mapDispatchToProps = dispatch =>({
  addPlayer: player => dispatch(changeTeam(player)),

})

