import './pill.styles.scss'

const Pill= ({items, selectedItem,handlePositionChange}) =>{
    return (
        <div className='pill'>

       { items.map((item,index) => (console.log(item, selectedItem),<div id={item===selectedItem ? 'selected' : ''}className={`item ${index===0? 'first' : index===items.length-1 ? 'last' :'middle'}`} onClick={handlePositionChange}>{item }</div>))}

        </div>
    )
}
export default Pill
