import React from "react";
import { LineUp } from "../lineup/lineup.component";
import {addPlayer} from './set-lineup.utils'
import Pill from '../pill/pill.component'
import "./set-lineup.styles.scss";
import players from "./playerData";
import { CardList } from "../card-list/card-list.component";
import { SearchBox } from "../search-box/search-box.component.jsx";
import { Rosters } from "../rosters/rosters.component.jsx";
import CustomButton  from "../custom-button/custom-button.component.jsx";
import { Form } from "react-bootstrap";

class SetLineup extends React.Component {
  constructor() {
    super();
    this.state = {
      players: players,
      searchField: "",
      team: "user",
      userTeam: {
        QB: { name: "" },
        RB1: { name: "" },
        RB2: { name: "" },
        WR1: { name: "" },
        WR2: { name: "" },
        TE: { name: "" },
        Flex: { name: "" },
        K: { name: "" },
        DE: { name: "" },
      },
      opponentTeam: {
        QB: { name: "" },
        RB1: { name: "" },
        RB2: { name: "" },
        WR1: { name: "" },
        WR2: { name: "" },
        TE: { name: "" },
        Flex: { name: "" },
        K: { name: "" },
        DE: { name: "" },
      },
      teamsFull:false,
      positions:['All','QB','RB','WR','TE','K','DE'],
      positionSelected:'All'
    };
  }
  handleChange = (e) => {
    this.setState({ searchField: e.target.value });
  };
  handlePositionChange = (newPosition) => {
    this.setState({positionSelected:newPosition})
  }
  addPlayer = (player, position, team) => {
    if (this.state.team === "user") {
      this.setState((prevState) => {
        if (position === "RB") {
          let team = Object.assign({}, prevState.userTeam);
          if (team["RB1"]["name"] === "") {
            console.log("rb1 part fired");
            team["RB1"]["name"] = player;
          } else if (team["RB2"]["name"] === "") {
            if (team["RB1"]["name"] !== player) {
              team["RB2"]["name"] = player;
            }
          } else {
            if (
              team["RB2"]["name"] !== player &&
              team["RB1"]["name"] !== player
            ) {
              team["Flex"]["name"] = player;
            }
          }
        } else if (position === "WR") {
          let team = Object.assign({}, prevState.userTeam);
          if (team["WR1"]["name"] === "") {
            team["WR1"]["name"] = player;
          } else if (team["WR2"]["name"] === "") {
            if (team["WR1"]["name"] !== player) {
              team["WR2"]["name"] = player;
            }
          } else {
            if (team["WR1"]["name"]!==player && team["WR2"]["name"]!==player){
              team["Flex"]["name"] = player;
            }          }
        } else {
          let team = Object.assign({}, prevState.userTeam);
          team[position]["name"] = player;
          return { ...prevState, userTeam: team };
        }
      },()=>{

        let userTeamFull=true
        for (const prop in this.state.userTeam){
            if (this.state.userTeam[prop]['name']===''){
              userTeamFull=false
              console.log('test')
            }
        }

        let oppTeamFull=true
        for (const prop in this.state.opponentTeam){
            if (this.state.opponentTeam[prop]['name']===''){
              userTeamFull=false
            }
        }
        if (userTeamFull && oppTeamFull){
          this.setState({teamsFull:true})
        }
      });
    } else if (this.state.team === "opponent") {
     this.setState((prevState) => {
        if (position === "RB") {
          let team = Object.assign({}, prevState.opponentTeam);
          if (team["RB1"]["name"] === "") {
            console.log("rb1 part fired");
            team["RB1"]["name"] = player;
          } else if (team["RB2"]["name"] === "") {
            console.log("rb2 part fired");
            if (
              team["RB1"]["name"] !== player &&
              team["Flex"]["name"] !== player
            ) {
              team["RB2"]["name"] = player;
            }
          } else {
            if (
              team["RB2"]["name"] !== player &&
              team["RB1"]["name"] !== player
            ) {
              team["Flex"]["name"] = player;
            }
          }
        } else if (position === "WR") {
          let team = Object.assign({}, prevState.opponentTeam);
          if (team["WR1"]["name"] === "") {
            team["WR1"]["name"] = player;
          } else if (team["WR2"]["name"] === "") {
            if (team["WR1"]["name"] !== player) {
              team["WR2"]["name"] = player;
            }          } else {
            if (team["WR1"]["name"]!==player && team["WR2"]["name"]!==player){
              team["Flex"]["name"] = player;
            }     
          }
        } else {
          let team = Object.assign({}, prevState.opponentTeam);
          team[position]["name"] = player;
          return { ...prevState, opponentTeam: team };
        }
      }, ()=> {
        let userTeamFull=true
        for (const prop in this.state.userTeam){
            if (this.state.userTeam[prop]['name']===''){
              userTeamFull=false
              console.log('test')
            }
        }

        let oppTeamFull=true
        for (const prop in this.state.opponentTeam){
            if (this.state.opponentTeam[prop]['name']===''){
              userTeamFull=false
            }
        }
        if (userTeamFull && oppTeamFull){
          this.setState({teamsFull:true})
        }

      });
    }


  };

  handleClick = (e) => {
    if (e.target.id == "my-team" && this.state.team == "opponent") {
      this.setState({ team: "user" });
    }
    if (e.target.id === "opponent" && this.state.team === "user") {
      console.log(this.state.team);

      this.setState({ team: "opponent" });
      console.log(this.state.team);
    }
  };
  render() {
    const { searchField, players, positionSelected } = this.state;

    const positions = this.state.positions.map((ele)=> ele ==='DE' ? 'DEF' : ele)
    let filteredPlayers = players.filter((player) =>
      player.name.toLowerCase().includes(searchField.toLowerCase())
    );
    if (positionSelected==='All'){
     filteredPlayers = players.filter((player) =>
      player.name.toLowerCase().includes(searchField.toLowerCase())
    );}
    else{
       filteredPlayers = players.filter((player) =>
      player.name.toLowerCase().includes(searchField.toLowerCase())
    ).filter((player)=> (player.position ===positionSelected || (player.position=='DE' && positionSelected=='DEF')));
    }


    return (

      <div className="set-lineup">
        <div className="flex-box-col center main">
          <div className="search-box">

            {this.state.teamsFull ? <CustomButton> View  Lines </CustomButton> : ''}
          </div>

          <div className="flex-box-row">
          <div className='flex-box-col'>
          <div className="choose-lineup">
            <div
              id="my-team"
              className={`${
                this.state.team === "user" ? "active" : ""
              } lineup-choice my-team`}
              onClick={this.handleClick}
            >
              My Team
            </div>
            <div
              id="opponent"
              className={`${
                this.state.team === "opponent" ? "active" : ""
              } lineup-choice opponent`}
              onClick={this.handleClick}
            >
              Opposing Team
            </div>
          </div>
            <div className="roster">
              <Rosters
                team={this.state.team}
                userTeam={this.state.userTeam}
                opponentTeam={this.state.opponentTeam}
              />
            </div>
            </div>

            <div className="player-options">

             <div className = 'player-options-top'>
              <Pill items={positions} selectedItem={this.state.positionSelected} handlePositionChange={this.handlePositionChange}/>
              <SearchBox
              placeholder="Search Player"
              handleChange={this.handleChange}
            />
            </div>
              <CardList players={filteredPlayers} addPlayer={this.addPlayer} />
            </div>
          </div>
        </div>
      </div>
    );
  }
}
export default SetLineup;
