import React from 'react';
import './custom-dropdown.styles.scss'

export const CustomDropdown = ({options, defaultOption, handleChange}) =>{
return(
    <div className='custom-dropdown'>

    <select name="player-quantity" onChange={handleChange}>
    {
     options.map((option)=> option===defaultOption ? <option selected value={option}>{option}</option>:<option value={option}>{option}</option>)  
    }
      </select>
    </div>
)
}
