import {createSelector} from 'reselect';

export const selectUserTeam = (state) =>state.userTeam;
export const selectOpponentTeam = (state) => state.opponent;
export const selectUserPlayers = createSelector(
    [selectUserTeam],
    (players)=>Object.keys(players).map(player => players[player]['name'])
)

