import React from "react";
import { LineUp } from "../../components/lineup/lineup.component";
import {addPlayer} from './set-lineup.utils'
import Pill from '../../components/pill/pill.component'
import "./set-lineup.styles.scss";
import players from "../../pages/set-lineup/playerData";
import { CardList } from "../../components/card-list/card-list.component";
import { SearchBox } from "../../components/search-box/search-box.component.jsx";
import { Rosters } from "../../components/rosters/rosters.component.jsx";
import { Form } from "react-bootstrap";

class SetLineup extends React.Component {
  constructor() {
    super();
    this.state = {
      players: players,
      searchField: "",
      team: "user",
      userTeam: {
        QB: { name: "" },
        RB1: { name: "" },
        RB2: { name: "" },
        WR1: { name: "" },
        WR2: { name: "" },
        TE: { name: "" },
        Flex: { name: "" },
        K: { name: "" },
        DEF: { name: "" },
      },
      opponentTeam: {
        QB: { name: "" },
        RB1: { name: "" },
        RB2: { name: "" },
        WR1: { name: "" },
        WR2: { name: "" },
        TE: { name: "" },
        Flex: { name: "" },
        K: { name: "" },
        DEF: { name: "" },
      },
      positions:['All','QB','RB','WR','TE','K','DEF'],
      positionSelected:'All'
    };
  }
  handleChange = (e) => {
    this.setState({ searchField: e.target.value });
  };
  handlePositionChange = (newPosition) => {
    this.setState({positionSelected:newPosition})
  }
  addPlayer=addPlayer

  handleClick = (e) => {
    if (e.target.id == "my-team" && this.state.team == "opponent") {
      this.setState({ team: "user" });
    }
    if (e.target.id === "opponent" && this.state.team === "user") {
      console.log(this.state.team);

      this.setState({ team: "opponent" });
      console.log(this.state.team);
    }
  };
  render() {
    const { searchField, players, positionSelected } = this.state;
    let filteredPlayers = players.filter((player) =>
      player.name.toLowerCase().includes(searchField.toLowerCase())
    );
    if (positionSelected==='All'){
     filteredPlayers = players.filter((player) =>
      player.name.toLowerCase().includes(searchField.toLowerCase())
    );}
    else{
       filteredPlayers = players.filter((player) =>
      player.name.toLowerCase().includes(searchField.toLowerCase())
    ).filter((player)=> player.position ===positionSelected);
    }


    return (
      <div className="set-lineup">
        <div className="flex-box-col">
          <div className="search-box">
            <SearchBox
              placeholder="Search Player"
              handleChange={this.handleChange}
            />
          </div>

          <div className="flex-box-row">
          <div className='flex-box-col'>
          <div className="choose-lineup">
            <div
              id="my-team"
              className={`${
                this.state.team === "user" ? "active" : ""
              } lineup-choice my-team`}
              onClick={this.handleClick}
            >
              My Team
            </div>
            <div
              id="opponent"
              className={`${
                this.state.team === "opponent" ? "active" : ""
              } lineup-choice opponent`}
              onClick={this.handleClick}
            >
              Opposing Team
            </div>
          </div>
            <div className="roster">
              <Rosters
                team={this.state.team}
                userTeam={this.state.userTeam}
                opponentTeam={this.state.opponentTeam}
              />
            </div>
            </div>

            <div className="player-options">
              <Pill items={this.state.positions} selectedItem={this.state.positionSelected} handlePositionChange={this.handlePositionChange}/>
              <CardList players={filteredPlayers} addPlayer={this.addPlayer} />
            </div>
          </div>
        </div>
      </div>
    );
  }
}
export default SetLineup;
