import React, {Component} from 'react';
import './App.css';
import players from './playerData.js'
import {CardList} from './components/card-list/card-list.component'
import {SearchBox} from './components/search-box/search-box.component.jsx'
class App extends Component {
  constructor(){
    super()
    this.state={
      players:players,
      searchField:''

    }
  }
  handleChange = (e)=>{

    this.setState({searchField:e.target.value})
  }
  render() {
    const{searchField,players}=this.state;
    const filteredPlayers=players.filter(player=>player.name.toLowerCase().includes(searchField.toLowerCase()))
    return(
    <div className="App">
    <h1>Fantasy Action</h1>
    <SearchBox placeholder='Search Player' handleChange = {this.handleChange}/>
    <CardList players={filteredPlayers}/>
    </div>
    )
  }
}

export default App;
