import React from 'react';
import {LineUp} from '../../components/lineup/lineup.component'
import './set-lineup.styles.scss'
import players from "../../pages/set-lineup/playerData";
import { CardList } from "../../components/card-list/card-list.component";
import { SearchBox } from "../../components/search-box/search-box.component.jsx";
import {Rosters} from '../../components/rosters/rosters.component.jsx'
class SetLineup extends React.Component{
    constructor() {
        super();
        this.state = {
          players: players,
          searchField: "",
          team: 'user',
          userTeam:{QB:{}, RB1:{}, RB2:{}, WR1:{}, WR2:{}, TE:{}, Flex:{}, Def:{}},
          opponentTeam:{QB:{}, RB1:{}, RB2:{}, WR1:{}, WR2:{}, TE:{}, Flex:{}, Def:{}}
      }}
      handleChange = (e) => {
        this.setState({ searchField: e.target.value });
      };
      addPlayer = (player, position, team) =>{

        if (this.state.team === 'user'){

            this.setState(prevState => {
              // creating copy of state variable jasper
              switch (position){
                case 'RB':
                  if (this.state.userTeam['RB1']==={}){
                    let team = Object.assign({}, prevState.userTeam); 
                    team['RB1']['name']=player
                  }
                default:
                  let team = Object.assign({}, prevState.userTeam); 
                  team[position]['name'] = player;                     // update the name property, assign a new value                 
                  return { ...prevState, userTeam:team };  
              }
                                            // return new object jasper object
            })

        }
        else if (this.state.team === 'opponent'){

            this.setState(prevState => {
              let team = Object.assign({}, prevState.opponentTeam);  // creating copy of state variable jasper
              team[position]['name'] = player;                     // update the name property, assign a new value                 
              return { ...prevState, opponentTeam:team };                                 // return new object jasper object
            })


        }
      }
      handleClick = (e) =>{
        if (e.target.id=='my-team' && this.state.team=='opponent'){
          this.setState({team:'user'})

        }
        if (e.target.id==='opponent' && this.state.team==='user'){
          console.log(this.state.team)

          this.setState({team:'opponent'})
          console.log(this.state.team)

        }
      };
      render() {
        console.log(this.state.team)
        const{searchField,players}=this.state;
        const filteredPlayers=players.filter(player=>player.name.toLowerCase().includes(searchField.toLowerCase()))
    return (
        <div className="set-lineup">
        <div className='flex-box-col'>
        <div className='search-box'>
        <SearchBox
        placeholder="Search Player"
        handleChange={this.handleChange}
      />
      </div>
      <div className='choose-lineup'>
       <div id='my-team' className= {`${this.state.team==='user' ? 'active':''} lineup-choice my-team`} onClick={this.handleClick}>My Team</div>
       <div id='opponent' className = {`${this.state.team==='opponent' ? 'active':''} lineup-choice opponent`} onClick={this.handleClick} >Opposing Team</div>
      </div>
      <div className='flex-box-row'>

        <div className='roster'>
      <Rosters team={this.state.team} userTeam={this.state.userTeam} opponentTeam={this.state.opponentTeam}/>
        </div>

        <div className='player-options'>
        <CardList players={filteredPlayers} addPlayer={this.addPlayer}/>
        </div>
        </div>
        </div>
      </div>


    )}}
export default SetLineup
