import React from "react";
import { LineUp } from "../lineup/lineup.component";
import {addPlayer} from './set-lineup.utils'
import Pill from '../pill/pill.component'
import "./set-lineup.styles.scss";
import players from "./playerData";

import { Rosters } from "../rosters/rosters.component.jsx";
import CustomButton  from "../custom-button/custom-button.component.jsx";
import { Form } from "react-bootstrap";
import {createStructuredSelector} from 'reselect'
import {connect} from 'react-redux'
import {selectUserTeam} from '../../redux/lineup/lineup.selectors.js'
import {selectOpponentTeam} from '../../redux/lineup/lineup.selectors.js'
import {PlayerOptions} from '../player-options/player-options.component'


const setLineup = ({userTeam, opponentTeam, team, players, searchField, teamsFull,positions, positionSelected}) => {

    return (

      <div className="set-lineup">
        <div className="flex-box-col center main">
          <div className="search-box">

            {this.state.teamsFull ? <CustomButton> View  Lines </CustomButton> : ''}
          </div>

          <div className="flex-box-row">
          <div className='flex-box-col'>
          <div className="choose-lineup">
            <div
              id="my-team"
              className={`${
                this.state.team === "user" ? "active" : ""
              } lineup-choice my-team`}
              onClick={this.handleClick}
            >
              My Team
            </div>
            <div
              id="opponent"
              className={`${
                this.state.team === "opponent" ? "active" : ""
              } lineup-choice opponent`}
              onClick={this.handleClick}
            >
              Opposing Team
            </div>
          </div>
            <div className="roster">
              <Rosters
                team={this.state.team}
                userTeam={this.state.userTeam}
                opponentTeam={this.state.opponentTeam}
                dropPlayer={this.dropPlayer}
              />
            </div>
            </div>
              <PlayerOptions filteredPlayers={filteredPlayers} addPlayer={this.addPlayer}  positions={positions} positionSelected={this.state.positionSelected} handlePositionChange={this.handlePositionChange}/>

          </div>
        </div>
      </div>
    );
  }




const mapStateToProps = (state)=>({
  userTeam: state.userTeam,
  opponentTeam: state.opponentTeam,
  team:state.team,
  players:state.players,
  searchField:state.searchField,
  teamsFull:state.teamsFull,
  positions:state.positions,
  positionSelected:state.positionSelected


})


export default connect(mapStateToProps)(SetLineup);
