export const changeTeam = (currentTeam, newTeam) => {
  if (newTeam == "my-team" && currentTeam == "opponent") {
    return "user";
  }
  if (newTeam === "opponent" && currentTeam === "user") {
    return "opponent";
  } else return currentTeam;
};

