      if (team["RB1"]["name"] !== player.player) {
        tempTeam["RB2"]["name"] = player.player;
        return tempTeam;
      }
    } else {
      if (
        team["RB2"]["name"] !== player &&
        team["RB1"]["name"] !== player
      ) {
        tempTeam["Flex"]["name"] = player.player;
        return tempTeam;
      }
    }
  }else if (player.position === "WR") {
    if (team["WR1"]["name"] === "") {
      tempTeam["WR1"]["name"] = player.player;
      return tempTeam;
    } else if (team["WR2"]["name"] === "") {
      if (team["WR1"]["name"] !== player) {
        tempTeam["WR2"]["name"] = player.player;
        return tempTeam;
      }
    } else {
      if (
        team["WR1"]["name"] !== player &&
        team["WR2"]["name"] !== player
      ) {
        tempTeam["Flex"]["name"] = player.player;
        return tempTeam;
      }
    }
  } else {
    tempTeam[player.position]["name"] = player.player;
    return tempTeam;
  }
};

